sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
   //Type appropriate comment here, and begin script below
	var todayDate = new Date();
	var formatTodayDate = formatDate(todayDate, g_user_date_time_format);
	if (newValue < formatTodayDate) {
		alert("Please enter valid Start Date. You cannot enter past date");
	}
	var endDate = g_form.getValue('u_end_date');
	if (endDate && (newValue > endDate)) {
		alert("Please enter Valid Start Date. You cannot enter the date after End date");
	}
}
